// --- ANIMATED BACKGROUND (GSAP gradient + minimal particles) ---
import gsap from 'gsap'

function AnimatedBG() {
  const bgRef = useRef<HTMLDivElement>(null)
  useEffect(() => {
    if (!bgRef.current) return
    const tl = gsap.timeline({ repeat: -1, yoyo: true })
    tl.to(bgRef.current, {
      background:
        'linear-gradient(120deg,#0f2027,#2c5364 45%,#005c97 90%)',
      duration: 6,
      ease: 'power2.inOut',
    })
    tl.to(
      bgRef.current,
      {
        background:
          'linear-gradient(120deg,#3a7bd5 10%,#3a6073 55%,#e96443 90%)',
        duration: 8,
        ease: 'power2.inOut',
      },
      '+=0'
    )
    tl.to(
      bgRef.current,
      {
        background:
          'linear-gradient(120deg,#373B44 10%,#4286f4 70%,#e96443 100%)',
        duration: 7,
        ease: 'power2.inOut',
      },
      '+=0'
    )
    return () => tl.kill()
  }, [])
  return (
    <div
      ref={bgRef}
      style={{
        position: 'fixed',
        zIndex: -2,
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        background: 'linear-gradient(120deg,#0f2027,#2c5364 55%,#005c97 100%)',
        transition: 'background 1.2s cubic-bezier(0.77,0,0.175,1)',
      }}
    />
  )
}

// --- PARTICLE OVERLAY BACKGROUND ---
function ParticlesBG() {
  const canvasRef = useRef<HTMLCanvasElement>(null)
  useEffect(() => {
    const canvas = canvasRef.current
    if (!canvas) return
    const ctx = canvas.getContext('2d')
    let w = window.innerWidth
    let h = window.innerHeight
    canvas.width = w
    canvas.height = h

    let particles = Array.from({ length: 28 }).map(() => ({
      x: Math.random() * w,
      y: Math.random() * h,
      r: 1 + Math.random() * 2,
      dx: (Math.random() - 0.5) * 0.35,
      dy: (Math.random() - 0.5) * 0.2,
    }))

    function draw() {
      ctx!.clearRect(0, 0, w, h)
      for (const p of particles) {
        ctx!.beginPath()
        ctx!.arc(p.x, p.y, p.r, 0, 2 * Math.PI)
        ctx!.fillStyle = '#0af9'
        ctx!.fill()
      }
    }
    function animate() {
      for (const p of particles) {
        p.x += p.dx
        p.y += p.dy
        if (p.x < 0 || p.x > w) p.dx *= -1
        if (p.y < 0 || p.y > h) p.dy *= -1
      }
      draw()
      requestAnimationFrame(animate)
    }
    animate()
    const handleResize = () => {
      w = window.innerWidth
      h = window.innerHeight
      canvas.width = w
      canvas.height = h
    }
    window.addEventListener('resize', handleResize)
    return () => window.removeEventListener('resize', handleResize)
  }, [])
  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100vw',
        height: '100vh',
        zIndex: -1,
        pointerEvents: 'none',
      }}
    />
  )
}

